children.tableData:
{{getUsers.data.map(e => {
	return {
		_id: e._id,
		firstname: e.firstname,
		lastname: e.lastname,
		email: e.email,
		role: e.role,
		active: e.active,
		createdAt: e.createdAt,
		updatedAt: e.updatedAt,
		roles: e.roles,
		attributes: e.attributes,
		role_items: e.role_items
	}
})}}
